        assembly ("memory-safe") {
            r.slot := store.slot
        }
    }
}

contracts/ILotteryData.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/**
 * @title ILotteryData
 * @dev Interface for reading lottery data from the minting contract
 */
interface ILotteryData {
    // Struct definitions (must match minting contract)
    struct Participant {
        address owner;
        uint256 lottoID;
        uint256 weightStart;
        uint256 weightEnd;
        uint256 tier;
    }

    struct LottoEntry {
        uint256 lottoID;
        uint256 weight;
    }

    // View functions
    function participants(uint256 index) external view returns (
        address owner,
        uint256 lottoID,
        uint256 weightStart,
        uint256 weightEnd,
        uint256 tier
    );

    function getParticipantCount() external view returns (uint256);
    function totalWeight() external view returns (uint256);
    function lotteryActive() external view returns (bool);
    function getLottoIDsByAddress(address user) external view returns (LottoEntry[] memory);
    function lottoIDIndexer(uint256 lottoID) external view returns (address);

    // State management functions (only callable by authorized contracts)
    function deactivateLottery() external;
    function activateLottery() external;
}

contracts/LotteryDrawManagerV2.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./ILotteryData.sol";

/**
 * @title LotteryDrawManagerV2
 * @dev Advanced lottery draw manager with:
 *      - Binary search optimization O(log n)
 *      - Four draw types with Bitcoin-style halving
 *      - Multi-asset prize buckets (ETH + multiple ERC20s)
 *      - Two-way query mechanics (weight→winner, address→wins)
 *      - Optional Chainlink VRF
 */
contract LotteryDrawManagerV2 is
    Initializable,
    OwnableUpgradeable,
    ReentrancyGuardUpgradeable,
    PausableUpgradeable,
    UUPSUpgradeable
{
    using SafeERC20 for IERC20;

    // ============ ENUMS ============

    enum RandomnessMode {
        PSEUDO_RANDOM,  // block.prevrandao (acceptable - users don't choose numbers)
        CHAINLINK_VRF   // Provably fair randomness (optional)
    }

    enum DrawType {
        WEEKLY,      // Every 7 days
        MONTHLY,     // Every 30 days
        QUARTERLY,   // Every 90 days
        YEARLY       // Every 365 days
    }

    // ============ STRUCTS ============

    struct PrizeBucket {
        uint256 ethAmount;                    // ETH in bucket
        address[] tokenAddresses;             // ERC20 token addresses
        mapping(address => uint256) tokenAmounts;  // Token address → amount
    }

    struct DrawConfig {
        DrawType drawType;
        uint256 initialPrizeAmount;    // Initial prize amount in wei
        uint256 currentPrizeAmount;    // Current prize after halvings
        uint256 halvingInterval;       // Number of draws before halving
        uint256 drawCount;             // Total draws executed
        uint256 lastDrawTime;          // Timestamp of last draw
        uint256 drawInterval;          // Time between draws (in seconds)
        bool active;                   // Is this draw type active
    }

    struct Draw {
        uint256 drawId;
        DrawType drawType;
        uint256 timestamp;
        uint256 prizeEth;              // ETH prize amount
        address winner;
        uint256 winningLottoID;
        uint256 winningNumber;
        uint256 participantCountAtDraw;
        uint256 totalWeightAtDraw;
        address[] prizeTokens;         // ERC20 tokens in prize
        mapping(address => uint256) prizeTokenAmounts;  // Token amounts
    }

    // ============ STATE VARIABLES ============

    ILotteryData public mintingContract;
    RandomnessMode public randomnessMode;

    // Draw configurations
    mapping(DrawType => DrawConfig) public drawConfigs;

    // Draw intervals (time between draws for each type)
    mapping(DrawType => uint256) public drawIntervals;

    // Draw history
    uint256 public totalDrawCount;
    mapping(uint256 => Draw) public draws;  // drawId → Draw

    // Prize buckets per draw type
    mapping(DrawType => PrizeBucket) private prizeBuckets;

    // User tracking: address → array of drawIds they won
    mapping(address => uint256[]) public userWins;

    // Reverse lookup: drawId → whether it's been drawn
    mapping(uint256 => bool) public drawExecuted;

    // Chainlink VRF (optional)
    address public vrfCoordinator;
    bytes32 public vrfKeyHash;
    uint64 public vrfSubscriptionId;
    uint32 public vrfCallbackGasLimit;
    uint16 public vrfRequestConfirmations;
    mapping(uint256 => uint256) public vrfRequestToDrawId;

    // ============ EVENTS ============

    event DrawConfigured(DrawType indexed drawType, uint256 initialPrize, uint256 halvingInterval, uint256 drawInterval);
    event DrawExecuted(uint256 indexed drawId, DrawType indexed drawType, address indexed winner, uint256 lottoID, uint256 prizeEth);
    event PrizeBucketFunded(DrawType indexed drawType, uint256 ethAmount, address[] tokens, uint256[] amounts);
    event RandomnessModeChanged(RandomnessMode oldMode, RandomnessMode newMode);
    event HalvingOccurred(DrawType indexed drawType, uint256 oldAmount, uint256 newAmount, uint256 drawNumber);
    event VRFConfigured(address coordinator, bytes32 keyHash, uint64 subscriptionId);

    // ============ CONSTRUCTOR ============

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Initialize the contract (replaces constructor for upgradeable contracts)
     */
    function initialize(address _mintingContract, RandomnessMode _randomnessMode) public initializer {
        __Ownable_init(msg.sender);
        __ReentrancyGuard_init();
        __Pausable_init();
        __UUPSUpgradeable_init();

        require(_mintingContract != address(0), "Invalid minting contract");
        mintingContract = ILotteryData(_mintingContract);
        randomnessMode = _randomnessMode;
        totalDrawCount = 0;

        // Initialize default VRF params
        vrfCallbackGasLimit = 2500000;
        vrfRequestConfirmations = 3;

        // Set draw intervals (in seconds)
        drawIntervals[DrawType.WEEKLY] = 7 days;
        drawIntervals[DrawType.MONTHLY] = 30 days;
        drawIntervals[DrawType.QUARTERLY] = 90 days;
        drawIntervals[DrawType.YEARLY] = 365 days;
    }

    // ============ CONFIGURATION FUNCTIONS ============

    /**
     * @dev Configure a draw type with halving schedule
     * @param _drawType The type of draw
     * @param _initialPrize Initial prize amount in wei
     * @param _halvingInterval Number of draws before halving occurs
     */
    function configureDrawType(
        DrawType _drawType,
        uint256 _initialPrize,
        uint256 _halvingInterval
    ) external onlyOwner {
        require(_initialPrize > 0, "Prize must be > 0");
        require(_halvingInterval > 0, "Halving interval must be > 0");

        uint256 interval;
        if (_drawType == DrawType.WEEKLY) {
            interval = 7 days;
        } else if (_drawType == DrawType.MONTHLY) {
            interval = 30 days;
        } else if (_drawType == DrawType.QUARTERLY) {
            interval = 90 days;
        } else {
            interval = 365 days;
        }

        DrawConfig storage config = drawConfigs[_drawType];
        config.drawType = _drawType;
        config.initialPrizeAmount = _initialPrize;
        config.currentPrizeAmount = _initialPrize;
        config.halvingInterval = _halvingInterval;
        config.drawCount = 0;
        config.lastDrawTime = block.timestamp;
        config.drawInterval = interval;
        config.active = true;

        emit DrawConfigured(_drawType, _initialPrize, _halvingInterval, interval);
    }

    /**
     * @dev Fund a prize bucket with ETH and/or ERC20 tokens
     * @param _drawType The draw type to fund
     * @param _tokens Array of ERC20 token addresses
     * @param _amounts Array of token amounts
     */
    function fundPrizeBucket(
        DrawType _drawType,
        address[] calldata _tokens,
        uint256[] calldata _amounts
    ) external payable onlyOwner {
        require(_tokens.length == _amounts.length, "Array length mismatch");

        PrizeBucket storage bucket = prizeBuckets[_drawType];

        // Add ETH
        if (msg.value > 0) {
            bucket.ethAmount += msg.value;
        }

        // Add ERC20 tokens
        for (uint256 i = 0; i < _tokens.length; i++) {
            require(_tokens[i] != address(0), "Invalid token address");
            require(_amounts[i] > 0, "Amount must be > 0");

            // Transfer tokens from sender to contract
            IERC20(_tokens[i]).safeTransferFrom(msg.sender, address(this), _amounts[i]);

            // Update bucket
            if (bucket.tokenAmounts[_tokens[i]] == 0) {
                bucket.tokenAddresses.push(_tokens[i]);
            }
            bucket.tokenAmounts[_tokens[i]] += _amounts[i];
        }

        emit PrizeBucketFunded(_drawType, msg.value, _tokens, _amounts);
    }

    /**
     * @dev Set randomness mode
     */
    function setRandomnessMode(RandomnessMode _mode) external onlyOwner {
        RandomnessMode oldMode = randomnessMode;
        randomnessMode = _mode;
        emit RandomnessModeChanged(oldMode, _mode);
    }

    /**
     * @dev Configure Chainlink VRF
     */
    function configureVRF(
        address _coordinator,
        bytes32 _keyHash,
        uint64 _subscriptionId,
        uint32 _callbackGasLimit,
        uint16 _requestConfirmations
    ) external onlyOwner {
        require(_coordinator != address(0), "Invalid coordinator");
        vrfCoordinator = _coordinator;
        vrfKeyHash = _keyHash;
        vrfSubscriptionId = _subscriptionId;
        vrfCallbackGasLimit = _callbackGasLimit;
        vrfRequestConfirmations = _requestConfirmations;
        emit VRFConfigured(_coordinator, _keyHash, _subscriptionId);
    }

    /**
     * @dev Toggle draw type active status
     */
    function setDrawTypeActive(DrawType _drawType, bool _active) external onlyOwner {
        drawConfigs[_drawType].active = _active;
    }

    // ============ DRAW EXECUTION ============

    /**
     * @dev Execute a draw for a specific draw type
     * @param _drawType The type of draw to execute
     */
    function executeDraw(DrawType _drawType) external onlyOwner nonReentrant whenNotPaused {
        DrawConfig storage config = drawConfigs[_drawType];
        require(config.active, "Draw type not active");
        require(config.initialPrizeAmount > 0, "Draw type not configured");
        require(
            block.timestamp >= config.lastDrawTime + config.drawInterval,
            "Draw interval not elapsed"
        );

        // Get participant data from minting contract
        uint256 participantCount = mintingContract.getParticipantCount();
        uint256 totalWeight = mintingContract.totalWeight();
        require(participantCount > 0, "No participants");
        require(totalWeight > 0, "No weight");

        // Check if halving should occur
        if (config.drawCount > 0 && config.drawCount % config.halvingInterval == 0) {
            uint256 oldAmount = config.currentPrizeAmount;
            config.currentPrizeAmount = config.currentPrizeAmount / 2;
            emit HalvingOccurred(_drawType, oldAmount, config.currentPrizeAmount, config.drawCount);
        }

        // Create draw record
        totalDrawCount++;
        uint256 drawId = totalDrawCount;

        Draw storage draw = draws[drawId];
        draw.drawId = drawId;
        draw.drawType = _drawType;
        draw.timestamp = block.timestamp;
        draw.participantCountAtDraw = participantCount;
        draw.totalWeightAtDraw = totalWeight;

        // Update config
        config.drawCount++;
        config.lastDrawTime = block.timestamp;

        // Generate random number and complete draw
        if (randomnessMode == RandomnessMode.PSEUDO_RANDOM) {
            uint256 randomNumber = _generatePseudoRandom(totalWeight, drawId);
            _completeDraw(drawId, randomNumber);
        } else {
            // Chainlink VRF path (to be called from callback)
            _requestVRFRandomness(drawId);
        }
    }

    /**
     * @dev Complete a draw with a random number (internal)
     */
    function _completeDraw(uint256 _drawId, uint256 _randomNumber) internal {
        Draw storage draw = draws[_drawId];
        require(!drawExecuted[_drawId], "Draw already executed");

        DrawConfig storage config = drawConfigs[draw.drawType];
        PrizeBucket storage bucket = prizeBuckets[draw.drawType];

        // Select winner using OPTIMIZED BINARY SEARCH
        (address winner, uint256 winningLottoID) = _selectWinnerBinarySearch(
            _randomNumber,
            draw.totalWeightAtDraw
        );

        // Update draw
        draw.winner = winner;
        draw.winningLottoID = winningLottoID;
        draw.winningNumber = _randomNumber;
        drawExecuted[_drawId] = true;

        // Record user win
        userWins[winner].push(_drawId);

        // Determine prize amount (use config or bucket, whichever is less)
        uint256 ethPrize = config.currentPrizeAmount;
        if (bucket.ethAmount < ethPrize) {
            ethPrize = bucket.ethAmount;
        }

        draw.prizeEth = ethPrize;

        // Transfer ETH prize
        if (ethPrize > 0) {
            bucket.ethAmount -= ethPrize;
            (bool success, ) = payable(winner).call{value: ethPrize}("");
            require(success, "ETH transfer failed");
        }

        // Transfer ERC20 prizes (distribute all tokens in bucket proportionally)
        address[] memory tokenAddrs = bucket.tokenAddresses;
        for (uint256 i = 0; i < tokenAddrs.length; i++) {
            address token = tokenAddrs[i];
            uint256 amount = bucket.tokenAmounts[token];

            if (amount > 0) {
                // For now, give all tokens to winner
                // (Can be adjusted to percentage-based distribution)
                bucket.tokenAmounts[token] = 0;
                IERC20(token).safeTransfer(winner, amount);

                // Record in draw
                draw.prizeTokens.push(token);
                draw.prizeTokenAmounts[token] = amount;
            }
        }

        // Clear token addresses if all distributed
        delete bucket.tokenAddresses;

        emit DrawExecuted(_drawId, draw.drawType, winner, winningLottoID, ethPrize);
    }

    // ============ OPTIMIZED WINNER SELECTION (BINARY SEARCH) ============

    /**
     * @dev Select winner using BINARY SEARCH - O(log n) complexity
     * @param _randomNumber Random number in range [0, totalWeight)
     * @param _totalWeight Total weight at draw time
     * @return winner Address of winner
     * @return lottoID Winning lottery ID
     */
    function _selectWinnerBinarySearch(uint256 _randomNumber, uint256 _totalWeight)
        internal
        view
        returns (address winner, uint256 lottoID)
    {
        require(_randomNumber < _totalWeight, "Invalid random number");

        uint256 participantCount = mintingContract.getParticipantCount();
        require(participantCount > 0, "No participants");

        uint256 left = 0;
        uint256 right = participantCount - 1;

        // Binary search to find participant whose range contains _randomNumber
        while (left <= right) {
            uint256 mid = left + (right - left) / 2;

            (
                address owner,
                uint256 lottoId,
                uint256 weightStart,
                uint256 weightEnd,
                // tier
            ) = mintingContract.participants(mid);

            if (_randomNumber < weightStart) {
                // Winner is in left half
                if (mid == 0) break;
                right = mid - 1;
            } else if (_randomNumber >= weightEnd) {
                // Winner is in right half
                left = mid + 1;
            } else {
                // Found the winner!
                return (owner, lottoId);
            }
        }

        revert("Winner not found");
    }

    // ============ RANDOMNESS GENERATION ============

    /**
     * @dev Generate pseudo-random number
     */
    function _generatePseudoRandom(uint256 _maxValue, uint256 _drawId)
        internal
        view
        returns (uint256)
    {
        return uint256(
            keccak256(
                abi.encodePacked(
                    block.timestamp,
                    block.prevrandao,
                    msg.sender,
                    _drawId,
                    _maxValue,
                    blockhash(block.number - 1)
                )
            )
        ) % _maxValue;
    }

    /**
     * @dev Request randomness from Chainlink VRF
     */
    function _requestVRFRandomness(uint256 _drawId) internal {
        require(vrfCoordinator != address(0), "VRF not configured");
        // TODO: Actual Chainlink VRF integration
        // This requires inheriting from VRFConsumerBaseV2
        // For now, revert with helpful message
        revert("Chainlink VRF not yet integrated - use PSEUDO_RANDOM mode");
    }

    // ============ TWO-WAY QUERY MECHANICS ============

    /**
     * @dev Check if a user won any draws
     * @param _user User address
     * @return drawIds Array of draw IDs the user won
     */
    function getUserWins(address _user) external view returns (uint256[] memory) {
        return userWins[_user];
    }

    /**
     * @dev Check if a user won a specific draw
     * @param _user User address
     * @param _drawId Draw ID
     * @return won Whether user won this draw
     */
    function didUserWin(address _user, uint256 _drawId) external view returns (bool) {
        if (!drawExecuted[_drawId]) return false;
        return draws[_drawId].winner == _user;
    }

    /**
     * @dev Get all NFTs (lottoIDs) owned by a user
     * @param _user User address
     * @return entries Array of lottery entries
     */
    function getUserLotteryEntries(address _user)
        external
        view
        returns (ILotteryData.LottoEntry[] memory)
    {
        return mintingContract.getLottoIDsByAddress(_user);
    }

    /**
     * @dev Check which of user's NFTs won which draws
     * @param _user User address
     * @return drawIds Array of draw IDs
     * @return winningLottoIDs Array of winning lottery IDs
     */
    function getUserWinDetails(address _user)
        external
        view
        returns (uint256[] memory drawIds, uint256[] memory winningLottoIDs)
    {
        uint256[] memory wins = userWins[_user];
        uint256 count = wins.length;

        drawIds = new uint256[](count);
        winningLottoIDs = new uint256[](count);

        for (uint256 i = 0; i < count; i++) {
            uint256 drawId = wins[i];
            drawIds[i] = drawId;
            winningLottoIDs[i] = draws[drawId].winningLottoID;
        }

        return (drawIds, winningLottoIDs);
    }

    /**
     * @dev Get draw details including prizes
     * @param _drawId Draw ID
     * @return drawType Type of draw
     * @return timestamp Time of draw
     * @return winner Winner address
     * @return winningLottoID Winning lottery ID
     * @return prizeEth ETH prize amount
     * @return prizeTokens Array of prize token addresses
     */
    function getDrawDetails(uint256 _drawId)
        external
        view
        returns (
            DrawType drawType,
            uint256 timestamp,
            address winner,
            uint256 winningLottoID,
            uint256 prizeEth,
            address[] memory prizeTokens
        )
    {
        Draw storage draw = draws[_drawId];
        return (
            draw.drawType,
            draw.timestamp,
            draw.winner,
            draw.winningLottoID,
            draw.prizeEth,
            draw.prizeTokens
        );
    }

    /**
     * @dev Get prize token amount for a specific draw and token
     */
    function getDrawPrizeTokenAmount(uint256 _drawId, address _token)
        external
        view
        returns (uint256)
    {
        return draws[_drawId].prizeTokenAmounts[_token];
    }

    /**
     * @dev Get prize bucket status for a draw type
     */
    function getPrizeBucketStatus(DrawType _drawType)
        external
        view
        returns (uint256 ethAmount, address[] memory tokens, uint256[] memory amounts)
    {
        PrizeBucket storage bucket = prizeBuckets[_drawType];
        ethAmount = bucket.ethAmount;
        tokens = bucket.tokenAddresses;

        amounts = new uint256[](tokens.length);
        for (uint256 i = 0; i < tokens.length; i++) {
            amounts[i] = bucket.tokenAmounts[tokens[i]];
        }

        return (ethAmount, tokens, amounts);
    }

    /**
     * @dev Get draw configuration
     */
    function getDrawConfig(DrawType _drawType)
        external
        view
        returns (
            uint256 initialPrize,
            uint256 currentPrize,
            uint256 halvingInterval,
            uint256 drawCount,
            uint256 lastDrawTime,
            uint256 drawInterval,
            uint256 nextDrawTime,
            bool active
        )
    {
        DrawConfig storage config = drawConfigs[_drawType];
        return (
            config.initialPrizeAmount,
            config.currentPrizeAmount,
            config.halvingInterval,
            config.drawCount,
            config.lastDrawTime,
            config.drawInterval,
            config.lastDrawTime + config.drawInterval,
            config.active
        );
    }

    // ============ ADMIN FUNCTIONS ============

    /**
     * @dev Emergency withdraw (only for excess funds not allocated to buckets)
     */
    function emergencyWithdraw(address _token, uint256 _amount)
        external
        onlyOwner
        nonReentrant
    {
        if (_token == address(0)) {
            // ETH
            (bool success, ) = payable(owner()).call{value: _amount}("");
            require(success, "ETH transfer failed");
        } else {
            // ERC20
            IERC20(_token).safeTransfer(owner(), _amount);
        }
    }

    /**
     * @dev Pause contract
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Get comprehensive system configuration
     */
    function getSystemConfig() external view returns (
        address _mintingContract,
        RandomnessMode _randomnessMode,
        uint256 _totalDrawCount,
        bool _paused
    ) {
        return (
            address(mintingContract),
            randomnessMode,
            totalDrawCount,
            paused()
        );
    }

    /**
     * @dev Get all draw intervals
     */
    function getAllDrawIntervals() external view returns (
        uint256 weekly,
        uint256 monthly,
        uint256 quarterly,
        uint256 yearly
    ) {
        return (
            drawIntervals[DrawType.WEEKLY],
            drawIntervals[DrawType.MONTHLY],
            drawIntervals[DrawType.QUARTERLY],
            drawIntervals[DrawType.YEARLY]
        );
    }

    /**
     * @dev Set draw interval for a specific draw type (admin control)
     */
    function setDrawInterval(DrawType _drawType, uint256 _interval) external onlyOwner {
        require(_interval > 0, "Interval must be > 0");
        drawIntervals[_drawType] = _interval;
    }

    /**
     * @dev Allow contract to receive ETH
     */
    receive() external payable {}

    /**
     * @dev Required by UUPS - only owner can upgrade
     */
    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}
}

contracts/RewardPoints.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

/**
 * @title RewardPoints
 * @dev ERC20 token that can ONLY be used for NFT lottery tickets and game bets (UUPS Upgradeable)
 *      Can only be minted by the RewardPointsManager contract
 *      Non-transferable to prevent secondary markets
 */
contract RewardPoints is Initializable, ERC20Upgradeable, OwnableUpgradeable, UUPSUpgradeable {

    // Authorized contracts that can accept reward points as payment
    mapping(address => bool) public authorizedSpenders;

    // The RewardPointsManager contract (only one that can mint)
    address public rewardManager;

    event AuthorizedSpenderAdded(address indexed spender);
    event AuthorizedSpenderRemoved(address indexed spender);
    event RewardManagerSet(address indexed manager);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Initialize the contract
     */
    function initialize() public initializer {
        __ERC20_init("HODL Reward Points", "HPOINTS");
        __Ownable_init(msg.sender);
        __UUPSUpgradeable_init();
    }

    /**
     * @dev Set the reward manager (can only be set once for security)
     */
    function setRewardManager(address _manager) external onlyOwner {
        require(rewardManager == address(0), "Manager already set");
        require(_manager != address(0), "Invalid manager address");
        rewardManager = _manager;
        emit RewardManagerSet(_manager);
    }

    /**
     * @dev Add authorized spender (NFT minter, game contracts, etc.)
     */
    function addAuthorizedSpender(address _spender) external onlyOwner {
        require(_spender != address(0), "Invalid spender");
        authorizedSpenders[_spender] = true;
        emit AuthorizedSpenderAdded(_spender);
    }

    /**
     * @dev Remove authorized spender
     */
    function removeAuthorizedSpender(address _spender) external onlyOwner {
        authorizedSpenders[_spender] = false;
        emit AuthorizedSpenderRemoved(_spender);
    }

    /**
     * @dev Check if address is authorized spender
     */
    function isAuthorizedSpender(address _spender) external view returns (bool) {
        return authorizedSpenders[_spender];
    }

    /**
     * @dev Get all reward point info for dashboard
     */
    function getTokenInfo() external view returns (
        string memory tokenName,
        string memory tokenSymbol,
        uint8 tokenDecimals,
        address manager,
        address tokenOwner
    ) {
        return (
            name(),
            symbol(),
            decimals(),
            rewardManager,
            owner()
        );
    }

    /**
     * @dev Mint reward points - only callable by RewardManager
     */
    function mint(address _to, uint256 _amount) external {
        require(msg.sender == rewardManager, "Only reward manager can mint");
        _mint(_to, _amount);
    }

    /**
     * @dev Burn reward points when used for purchases
     */
    function burnFrom(address _from, uint256 _amount) external {
        require(authorizedSpenders[msg.sender], "Not authorized spender");
        _burn(_from, _amount);
    }

    /**
     * @dev Override transfer to make non-transferable (soulbound)
     *      Only minting (from zero address) and burning (to zero address) allowed
     */
    function _update(address from, address to, uint256 amount) internal override {
        require(
            from == address(0) || to == address(0) || authorizedSpenders[to],
            "Reward points are non-transferable"
        );
        super._update(from, to, amount);
    }

    /**
     * @dev Required by UUPS - only owner can upgrade
     */
    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}
}

contracts/RewardPointsManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface IRewardPoints {
    function mint(address to, uint256 amount) external;
}

/**
 * @title RewardPointsManager
 * @dev Manages reward points accrual for token holders (UUPS Upgradeable)
 *      - Users earn reward points for holding the HODL token
 *      - Rate = baseRate × multiplier (based on configurable tiers)
 *      - Users claim at their discretion (they pay gas, no DoS risk)
 *      - Simplified math: uses minimum balance between last claim and now
 *      - No balance history stored - encourages holding
 */
contract RewardPointsManager is
    Initializable,
    OwnableUpgradeable,
    ReentrancyGuardUpgradeable,
    PausableUpgradeable,
    UUPSUpgradeable
{

    // The token users must hold to earn rewards (e.g., HODL token)
    IERC20 public stakingToken;

    // The reward points token (minted by this contract)
    IRewardPoints public rewardPoints;

    // Base reward rate per token per second (in wei units for precision)
    // Example: 1e18 = 1 reward point per token per second
    uint256 public baseRewardRate;

    // Multiplier basis points divisor (10000 = 100%)
    uint256 public constant MULTIPLIER_DIVISOR = 10000;

    // Minimum claim interval to prevent spam (default: 1 hour)
    uint256 public minClaimInterval;

    // Multiplier tiers (index = tier, value = multiplier in basis points)
    // Example: [10000, 15000, 20000] = [1x, 1.5x, 2x]
    uint256[] public multiplierTiers;

    // Minimum balance thresholds for each multiplier tier
    // Example: [0, 1000e18, 10000e18] = tier 0 for any balance, tier 1 for 1000+, tier 2 for 10000+
    uint256[] public tierThresholds;

    // User claim data
    struct UserClaim {
        uint256 lastClaimTime;        // Last time user claimed
        uint256 balanceAtLastClaim;   // Balance at last claim (for min calculation)
        uint256 totalClaimed;          // Total reward points claimed
    }

    mapping(address => UserClaim) public userClaims;

    // Events
    event RewardsClaimed(address indexed user, uint256 amount, uint256 timeElapsed);
    event BaseRewardRateUpdated(uint256 oldRate, uint256 newRate);
    event MultiplierTierUpdated(uint256 tier, uint256 multiplier, uint256 threshold);
    event MultiplierTierRemoved(uint256 tier);
    event StakingTokenSet(address indexed token);
    event RewardPointsSet(address indexed token);
    event MinClaimIntervalUpdated(uint256 oldInterval, uint256 newInterval);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Initialize the contract (replaces constructor for upgradeable contracts)
     */
    function initialize(
        address _stakingToken,
        address _rewardPoints,
        uint256 _baseRewardRate
    ) public initializer {
        __Ownable_init(msg.sender);
        __ReentrancyGuard_init();
        __Pausable_init();
        __UUPSUpgradeable_init();

        require(_stakingToken != address(0), "Invalid staking token");
        require(_rewardPoints != address(0), "Invalid reward points");

        stakingToken = IERC20(_stakingToken);
        rewardPoints = IRewardPoints(_rewardPoints);
        baseRewardRate = _baseRewardRate;
        minClaimInterval = 1 hours; // Default: 1 hour

        // Initialize with default 1x multiplier
        multiplierTiers.push(10000); // 10000 basis points = 1x
        tierThresholds.push(0);      // Tier 0 starts at 0 balance
    }

    /**
     * @dev Users call this to claim their accumulated reward points
     *      Gas paid by user - no DoS risk for protocol
     */
    function claimRewards() external nonReentrant whenNotPaused {
        address user = msg.sender;
        uint256 currentBalance = stakingToken.balanceOf(user);
        require(currentBalance > 0, "No tokens to earn rewards");

        UserClaim storage claim = userClaims[user];

        // First time claiming - initialize
        if (claim.lastClaimTime == 0) {
            claim.lastClaimTime = block.timestamp;
            claim.balanceAtLastClaim = currentBalance;
            emit RewardsClaimed(user, 0, 0);
            return;
        }

        uint256 timeElapsed = block.timestamp - claim.lastClaimTime;
        require(timeElapsed >= minClaimInterval, "Claim too soon");

        // Use MINIMUM balance (current vs last claim) to discourage selling
        // This is the key gas optimization - no balance history needed!
        uint256 effectiveBalance = currentBalance < claim.balanceAtLastClaim
            ? currentBalance
            : claim.balanceAtLastClaim;

        // Calculate rewards: time × balance × baseRate × multiplier
        uint256 multiplier = _getMultiplierForBalance(effectiveBalance);
        uint256 rewards = (timeElapsed * effectiveBalance * baseRewardRate * multiplier) / (MULTIPLIER_DIVISOR * 1e18);

        require(rewards > 0, "No rewards to claim");

        // Update claim data
        claim.lastClaimTime = block.timestamp;
        claim.balanceAtLastClaim = currentBalance;
        claim.totalClaimed += rewards;

        // Mint reward points
        rewardPoints.mint(user, rewards);

        emit RewardsClaimed(user, rewards, timeElapsed);
    }

    /**
     * @dev View pending rewards for a user
     */
    function pendingRewards(address _user) external view returns (uint256) {
        uint256 currentBalance = stakingToken.balanceOf(_user);
        if (currentBalance == 0) return 0;

        UserClaim storage claim = userClaims[_user];
        if (claim.lastClaimTime == 0) return 0;

        uint256 timeElapsed = block.timestamp - claim.lastClaimTime;
        if (timeElapsed == 0) return 0;

        uint256 effectiveBalance = currentBalance < claim.balanceAtLastClaim
            ? currentBalance
            : claim.balanceAtLastClaim;

        uint256 multiplier = _getMultiplierForBalance(effectiveBalance);
        return (timeElapsed * effectiveBalance * baseRewardRate * multiplier) / (MULTIPLIER_DIVISOR * 1e18);
    }

    /**
     * @dev Get multiplier tier for a given balance
     */
    function _getMultiplierForBalance(uint256 _balance) internal view returns (uint256) {
        // Find highest tier the balance qualifies for
        for (uint256 i = tierThresholds.length; i > 0; i--) {
            if (_balance >= tierThresholds[i - 1]) {
                return multiplierTiers[i - 1];
            }
        }
        return multiplierTiers[0]; // Default to lowest tier
    }

    /**
     * @dev Get current multiplier for a user
     */
    function getUserMultiplier(address _user) external view returns (uint256) {
        uint256 balance = stakingToken.balanceOf(_user);
        return _getMultiplierForBalance(balance);
    }

    /**
     * @dev Get current multiplier tier index for a user
     */
    function getUserTier(address _user) external view returns (uint256) {
        uint256 balance = stakingToken.balanceOf(_user);
        for (uint256 i = tierThresholds.length; i > 0; i--) {
            if (balance >= tierThresholds[i - 1]) {
                return i - 1;
            }
        }
        return 0;
    }

    // ============ ADMIN FUNCTIONS ============

    /**
     * @dev Update base reward rate
     */
    function setBaseRewardRate(uint256 _newRate) external onlyOwner {
        uint256 oldRate = baseRewardRate;
        baseRewardRate = _newRate;
        emit BaseRewardRateUpdated(oldRate, _newRate);
    }

    /**
     * @dev Update minimum claim interval
     */
    function setMinClaimInterval(uint256 _interval) external onlyOwner {
        uint256 oldInterval = minClaimInterval;
        minClaimInterval = _interval;
        emit MinClaimIntervalUpdated(oldInterval, _interval);
    }

    /**
     * @dev Add or update a multiplier tier
     */
    function setMultiplierTier(
        uint256 _tierIndex,
        uint256 _multiplier,
        uint256 _threshold
    ) external onlyOwner {
        require(_multiplier >= 10000, "Multiplier must be >= 1x (10000)");
        require(_multiplier <= 100000, "Multiplier must be <= 10x (100000)");

        if (_tierIndex >= multiplierTiers.length) {
            multiplierTiers.push(_multiplier);
            tierThresholds.push(_threshold);
        } else {
            multiplierTiers[_tierIndex] = _multiplier;
            tierThresholds[_tierIndex] = _threshold;
        }

        emit MultiplierTierUpdated(_tierIndex, _multiplier, _threshold);
    }

    /**
     * @dev Remove the last multiplier tier
     */
    function removeLastTier() external onlyOwner {
        require(multiplierTiers.length > 1, "Cannot remove last tier");

        uint256 lastIndex = multiplierTiers.length - 1;
        multiplierTiers.pop();
        tierThresholds.pop();

        emit MultiplierTierRemoved(lastIndex);
    }

    /**
     * @dev Get number of tiers
     */
    function getTierCount() external view returns (uint256) {
        return multiplierTiers.length;
    }

    /**
     * @dev Get tier details
     */
    function getTier(uint256 _index) external view returns (uint256 multiplier, uint256 threshold) {
        require(_index < multiplierTiers.length, "Invalid tier index");
        return (multiplierTiers[_index], tierThresholds[_index]);
    }

    /**
     * @dev Get all tiers at once
     */
    function getAllTiers() external view returns (uint256[] memory multipliers, uint256[] memory thresholds) {
        return (multiplierTiers, tierThresholds);
    }

    /**
     * @dev Emergency pause
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Update staking token (use with extreme caution)
     */
    function setStakingToken(address _token) external onlyOwner {
        require(_token != address(0), "Invalid token");
        stakingToken = IERC20(_token);
        emit StakingTokenSet(_token);
    }

    /**
     * @dev Update reward points token (use with extreme caution)
     */
    function setRewardPointsToken(address _token) external onlyOwner {
        require(_token != address(0), "Invalid token");
        rewardPoints = IRewardPoints(_token);
        emit RewardPointsSet(_token);
    }

    /**
     * @dev Get user claim details
     */
    function getUserClaimData(address _user) external view returns (
        uint256 lastClaimTime,
        uint256 balanceAtLastClaim,
        uint256 totalClaimed,
        uint256 currentBalance,
        uint256 pendingReward,
        uint256 currentTier,
        uint256 currentMultiplier
    ) {
        UserClaim storage claim = userClaims[_user];
        currentBalance = stakingToken.balanceOf(_user);
        currentMultiplier = this.getUserMultiplier(_user);
        currentTier = this.getUserTier(_user);

        if (claim.lastClaimTime == 0 || currentBalance == 0) {
            return (claim.lastClaimTime, claim.balanceAtLastClaim, claim.totalClaimed, currentBalance, 0, currentTier, currentMultiplier);
        }

        uint256 timeElapsed = block.timestamp - claim.lastClaimTime;
        uint256 effectiveBalance = currentBalance < claim.balanceAtLastClaim
            ? currentBalance
            : claim.balanceAtLastClaim;
        uint256 multiplier = _getMultiplierForBalance(effectiveBalance);
        pendingReward = (timeElapsed * effectiveBalance * baseRewardRate * multiplier) / (MULTIPLIER_DIVISOR * 1e18);

        return (
            claim.lastClaimTime,
            claim.balanceAtLastClaim,
            claim.totalClaimed,
            currentBalance,
            pendingReward,
            currentTier,
            currentMultiplier
        );
    }

    /**
     * @dev Get comprehensive system stats
     */
    function getSystemStats() external view returns (
        uint256 _baseRewardRate,
        uint256 _minClaimInterval,
        uint256 _tierCount,
        address _stakingToken,
        address _rewardPoints,
        bool _paused
    ) {
        return (
            baseRewardRate,
            minClaimInterval,
            multiplierTiers.length,
            address(stakingToken),
            address(rewardPoints),
            paused()
        );
    }

    /**
     * @dev Required by UUPS - only owner can upgrade
     */
    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}
}

